import java.lang.String;
import java.lang.Object;
import edu.duke.*;
import java.io.*;

public class Part1 {
    public int findStopCodon (String dna, int startIndex, String stopCodon) {
        int currIndex = dna.indexOf(stopCodon, startIndex+3);

        while (currIndex != -1) {
            int diff = currIndex - startIndex;
            if (diff % 3 == 0) {
                return currIndex;
            } else {
                currIndex = dna.indexOf(stopCodon, currIndex+1);
            }
        }
        return dna.length();
    }

    public String findGene(String dna) {
        int startIndex = dna.indexOf("ATG");
        if (startIndex == -1) {
            return "";
        }
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");

        int minIndex = Math.min(taaIndex, Math.min(tagIndex, tgaIndex));

        if (minIndex == dna.length()){
            return "";
        }

        return dna.substring(startIndex, minIndex + 3);
    }

    public void testFindStopCodon () {
        //no TAA                             
        String dna = "atggatcctccatatacaacggtatctccacctcaggtttagatctcaacaacggaaccattgccgacatga";
        dna.toUpperCase();
        findStopCodon(dna, 0, "TAA");

        //valid                   |                    
        dna = "atggatcctccatatacaacgtaa";
        dna.toUpperCase();
        findStopCodon(dna, 0, "TAA");

        //TAA but not valid                          
        dna = "atggataacctccatatctcaacaacggaaccattgccgacatga";
        dna.toUpperCase();
        findStopCodon(dna, 0, "TAA");

    }

    public void testFindGene () {
        //no ATG                            
        String dna = "atatcctccatatacaacggtatctccacctcaggtttagatctcaacaacggaaccattgccgacata";
        dna.toUpperCase();
        findGene(dna);

        //ATG and one valid stop codon                |                    
        dna = "caatctgatggatcctccatatacaacgtaa";
        dna.toUpperCase();
        findGene(dna);

        //ATG and multiple valid stop codons                        
        dna = "atggactaacctccatagtctcaacaacggaaccattgccgacactga";
        dna.toUpperCase();
        findGene(dna);

        //ATG and no valid stop codons                       
        dna = "atggatacctccatatctcaacaacggaaccattgccgacata";
        dna.toUpperCase();
        findGene(dna);

    }

    public StorageResource getAllGenes (String dna) {
        //Create an empty StorageResource, call it geneList
        StorageResource geneList = new StorageResource();
        //Set startIndex to 0
        int startIndex = 0;
        //Repeat the following steps
        while (true){
            //Find the next gene after startIndex
            String currentGene = findGene(dna);
            //If no gene was found, leave this loop
            if (currentGene.isEmpty()) {
                break;
            }
            //Add that gene to geneList
            geneList.add(currentGene);
            //Set startIndex to just past the end of the gene
            startIndex = dna.indexOf(currentGene, startIndex); 
            currentGene.length();

        }
        //Your answer is geneList
        return geneList;
    }

    public void testOn(String dna) {
        System.out.println("Testing getAllGenes on " );
        StorageResource genes = getAllGenes(dna);
        for (String g: genes.data()) {
            System.out.println(g);
        }
    }

    public void test() {
        testOn("");
    }
}
